import * as types from './../constants/ActionTypes';

var initialState = [];

const products = (state=initialState, action) => {
    var index = -1;
    switch(action.type){
        case types.FETCH_PRODUCTS : 
            state = action.products;
            return [...state];
        case types.DELETE_PRODUCT :

        default : return state;
    }
}

export default products;
